import React, { useState, useEffect } from "react";
import axios from "axios";

import { Row, Button, Spinner, Dropdown } from "react-bootstrap";
import { Link, useHistory } from "react-router-dom";
import SubCategory from "./SubCategory";
import Title from "../layouts/Title";
export default function SubCategoryList() {
  const [isLoading, setIsLoading] = useState(true);
  const [categories, setCategories] = useState([]);
  const history = useHistory();

  console.log("categories :", categories);
  const createSubcategory = async () => {
    const { data } = await axios.post(`/ecom-subcategories`, {});
    let path = `/CreateSubcategory/${data._id}`;
    history.push(path);
  };
  const [subcategories, setSubcategories] = useState([]);
  useEffect(() => {
    const fetchsubcategories = async () => {
      const { data } = await axios.get("/ecom-subcategories");
      if (data) {
        setIsLoading(false);
      }
      localStorage.setItem("subcategories", JSON.stringify(data));
      setSubcategories(data);
    };
    const sub = JSON.parse(localStorage.getItem("products"));
    fetchsubcategories();
    const fetchcategories = async () => {
      const { data } = await axios.get("/ecom-categories");

      setCategories(data);
      localStorage.setItem("categorires", JSON.stringify(data));
    };
    fetchcategories();
  }, []);

  const filterbycategory = (name) => {
    const filteredSubcategories = sub.filter((product) => {
      return product.category === subcategory;
    });
    setSubcategories()
  };
  return (
    <>
      <Title name="Subcategory List" />
      <Button
        onClick={() => {
          createSubcategory();
        }}
      >
        Create Sub Category
      </Button>
      <div className="basic-dropdown">
        <Dropdown>
          <Dropdown.Toggle variant="primary">
            Filter By Sub Category
          </Dropdown.Toggle>
          <Dropdown.Menu>
            <Dropdown.Item
            // onClick={() => {
            //   setProducts(p);
            // }}
            >
              Default
            </Dropdown.Item>
            {categories.map((category) => {
              return (
                <Dropdown.Item
                  onClick={() => {
                    filterbycategory(category.name);
                  }}
                >
                  {category.name}
                </Dropdown.Item>
              );
            })}
          </Dropdown.Menu>
        </Dropdown>
      </div>
      <Row>
        {subcategories.map((subcategory, i) => {
          return <SubCategory subcategory={subcategory} key={i} />;
        })}
      </Row>
      {isLoading && <Spinner animation="border" variant="primary" />}
    </>
  );
}
