import React, { useState } from "react";

/// Scroll
import PerfectScrollbar from "react-perfect-scrollbar";

import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import { Link } from "react-router-dom";

const EventSidebar = ({ activeEvent }) => {
  const [value, onChange] = useState(new Date());

  return (
    <PerfectScrollbar
      className={`event-sidebar dz-scroll ${activeEvent ? "active" : ""}`}
      id="eventSidebar"
    >
      <div className="card rounded-0 mb-0 h-auto bg-transparent shadow-none">
        <div className="card-body event-calender pb-2 text-center">
          <Calendar onChange={onChange} value={value} />
        </div>
      </div>
      <div className="col-xl-6 col-lg-6 col-xxl-6 col-sm-6">
          <div className="card bg-success">
            <div className="card-body pb-0">
              <div className="row">
                <div className="col">
                  <h5 className="text-white">Power</h5>
                  <span className="text-white">2017.1.20</span>
                </div>
                <div className="col text-end">
                  <h5 className="text-white">
                    <i className="fa fa-caret-up"></i> 260
                  </h5>
                  <span className="text-white">+12.5(2.8%)</span>
                </div>
              </div>
            </div>
            <div className="chart-wrapper">
              <PowerLine />
            </div>
          </div>
        </div>
    </PerfectScrollbar>
  );
};

export default EventSidebar;
