import UploadImageContext from "./UploadImageContext";
import axios from "axios";

const UploadImageState = ({ children, setCloudImage }) => {
  const uploadFileHandler = async (e) => {
    setIsLoading(true);
    const file = e.target.files[0];

    const formData = new FormData();

    formData.append("file", file);
    formData.append("upload_preset", "sw2ks6ox");
    console.log(formData.get("file"));

    const { data } = await axios.post(
      "https://api.cloudinary.com/v1_1/djpdvrlkk/image/upload",
      formData
    );

    setCloudImage(data);
    if (data) {
      setIsLoading(false);
    }
  };
  console.log("uploadFileHandlerContext:", uploadFileHandler);
  return (
    <UploadImageContext.Provider value={{ uploadFileHandler }}>
      {children}
    </UploadImageContext.Provider>
  );
};

export default UploadImageState;
