import React, { useEffect, useState, useRef, useContext } from "react";
import axios from "axios";
import { useLocation, useHistory } from "react-router-dom";
import {
  Row,
  Col,
  Button,
  Dropdown,
  Spinner,
  Alert,
  Media,
  Form,
} from "react-bootstrap";

export default function CreateProduct() {
  const history = useHistory();
  const [subcategories, setSubcategories] = useState([]);
  const [products, setProducts] = useState();
  const [isCreated, setIsCreated] = useState(false);
  let id = window.location.href.slice(47);

  let a = products?.find((y) => {
    return y._id === id;
  });

  const [name, setName] = useState("");
  const [price, setPrice] = useState(0);
  const [quantity, setQuantity] = useState(0);
  const [brand, setBrand] = useState("");
  const [category, setCategory] = useState("");
  const [countInStock, setCountInStock] = useState(0);
  const [description, setDescription] = useState("");
  const [image, setImage] = useState("");
  const [cloudImage, setCloudImage] = useState();
  const [secondCloudImage, setSecondCloudImage] = useState();
  const [isLoading, setIsLoading] = useState(false);
  const handleSelect = (e) => {
    setCategory(e);
  };
  const uploadFileHandler = async (e) => {
    setIsLoading(true);
    const file = e.target.files[0];

    const formData = new FormData();

    formData.append("file", file);
    formData.append("upload_preset", "sw2ks6ox");
    console.log(formData.get("file"));

    const { data } = await axios.post(
      "https://api.cloudinary.com/v1_1/djpdvrlkk/image/upload",
      formData
    );

    setCloudImage(data);
    if (data) {
      setIsLoading(false);
    }
  };
  const secondaryuploadFileHandler = async (e) => {
    setIsLoading(true);
    const file = e.target.files[0];

    const formData = new FormData();

    formData.append("file", file);
    formData.append("upload_preset", "sw2ks6ox");
    console.log(formData.get("file"));

    const { data } = await axios.post(
      "https://api.cloudinary.com/v1_1/djpdvrlkk/image/upload",
      formData
    );

    setSecondCloudImage(data);
    if (data) {
      setIsLoading(false);
    }
  };

  const updateProduct = async (id) => {
    const res = await axios.put(`/ecom-product-list/${id}`, {
      name: name,
      category: category,
      descirption: description,
      price: price,
      quantity: quantity,
      image: cloudImage?.public_id,
      secondaryimage: secondCloudImage?.public_id,
    });

    if (res.status == 200) {
      alert("Success ! Product added");
      // setIsCreated(true);
    }
  };

  useEffect(() => {
    const fetchproducts = async () => {
      const { data } = await axios.get("/ecom-product-list");
      setProducts(data);
    };

    fetchproducts();

    const fetchsubcategories = async () => {
      const { data } = await axios.get("/ecom-subcategories");

      setSubcategories(data);
    };
    fetchsubcategories();
  }, []);

  return (
    <>
      <ol className="breadcrumb">
        <li className="breadcrumb-item active">
          <h1> hbferh</h1>
        </li>
        <li className="breadcrumb-item  ">
          <h1>Product List</h1>
        </li>
      </ol>
      <Form
        onSubmit={() => {
          updateProduct(a?._id, a?.name);
          let path = "/ecom-product-list";
          history.push(path);
        }}
      >
        <Row>
          {isCreated && (
            <Alert
              variant="success"
              className="alert-dismissible left-icon-big"
            >
              <Media>
                <div variant="" className="alert-left-icon-big">
                  <span>
                    <i className={`mdi mdi-check-circle-outline`}></i>
                  </span>
                </div>
                <Media.Body>
                  <h6 className="mt-1 mb-2">Success!</h6>
                  <p className="mb-0">New Item Created</p>
                </Media.Body>
              </Media>
            </Alert>
          )}
          {/* <form
          onSubmit={() => {
            updateProduct(a?._id, a?.name);
            let path = "/ecom-product-list";
            history.push(path);
          }}
        > */}
          <Col xl="6">
            <div class="card-body">
              <form>
                <div class="basic-form">
                  <div class="form-group mb-3">
                    <input
                      class="form-control form-control-lg"
                      type="text"
                      required
                      defaultValue={a?.name}
                      placeholder="Product Name"
                      onChange={(e) => {
                        console.log(e.target.value);
                        if (!e.target.value == "") {
                          setName(e.target.value);
                        } else {
                          setName(a?.name);
                        }
                      }}
                    />
                  </div>
                </div>
              </form>
            </div>
          </Col>
          <Col xl="6">
            <div class="card-body">
              <div class="basic-form">
                <form>
                  <div class="form-group mb-3">
                    <input
                      class="form-control form-control-lg"
                      type="text"
                      placeholder="Product Description"
                      onChange={(e) => setDescription(e.target.value)}
                    />
                  </div>
                </form>
              </div>
            </div>
          </Col>
          <Col xl="6">
            <div class="card-body">
              <div class="basic-form">
                <form>
                  <div class="form-group mb-3">
                    <input
                      class="form-control form-control-lg"
                      type="text"
                      placeholder="Category"
                      onChange={(e) => setBrand(e.target.value)}
                    />
                  </div>
                </form>
              </div>
            </div>
          </Col>

          <Col xl="6">
            <div class="card-body">
              <div class="basic-form">
                <form>
                  <div class="form-group mb-3">
                    <input
                      class="form-control form-control-lg"
                      type="number"
                      placeholder="Stock"
                      onChange={(e) => setCountInStock(e.target.value)}
                    />
                    {countInStock < 0 && (
                      <Alert
                        variant={`outline-danger`}
                        className=" alert-dismissible fade show"
                      >
                        Stock cant be negative
                      </Alert>
                    )}
                  </div>
                </form>
              </div>
            </div>
          </Col>
          <Col xl="6">
            <div class="card-body">
              <div class="basic-form">
                <form>
                  <div class="form-group mb-3">
                    <input
                      class="form-control form-control-lg"
                      type="number"
                      placeholder="Price"
                      onChange={(e) => setPrice(e.target.value)}
                    />
                  </div>
                </form>
              </div>
            </div>
          </Col>
          <Col xl="6">
            <div class="card-body">
              <div class="basic-form">
                <form>
                  <div class="form-group mb-3">
                    <input
                      class="form-control form-control-lg"
                      type="number"
                      placeholder="Quantity"
                      onChange={(e) => setQuantity(e.target.value)}
                    />
                  </div>
                </form>
              </div>
            </div>
          </Col>
          <Col xl="6">
            <div class="card-body">
              <div class="basic-form">
                <form>
                  <div class="form-group mb-3">
                    

                    {isLoading && (
                      <Spinner animation="border" variant="primary" />
                    )}
                  </div>
                </form>
              </div>
            </div>
          </Col>
          <Col xl="6">
            <div class="card-body">
              <div class="basic-form">
                <form>
                  <div class="form-group mb-3">
                    <input
                      class="form-control form-control-lg"
                      type="file"
                      placeholder="image"
                      name="image"
                      onChange={secondaryuploadFileHandler}
                    />

                    {isLoading && (
                      <Spinner animation="border" variant="primary" />
                    )}
                  </div>
                </form>
              </div>
            </div>
          </Col>
          <form className="needs-validation" noValidate="">
            <div className="row">
              <div className="col-md-6 mb-3">
                <label htmlFor="productname">Product Name</label>
                <input
                  type="text"
                  className="form-control"
                  id="productname"
                  placeholder=""
                  required
                />
                <div className="invalid-feedback">
                  Valid first name is required.
                </div>
              </div>
              <div className="col-md-6 mb-3">
                <label htmlFor="product description">Product description</label>
                <input
                  type="text"
                  className="form-control"
                  id="product description"
                  placeholder=""
                  required
                />
                <div className="invalid-feedback">
                  Valid last name is required.
                </div>
              </div>
              <div className="col-md-6 mb-3">
                <label htmlFor="product description">Product Image</label>
                <input
                  type="file"
                  id="image-file"
                  label="Choose File"
                  custom
                  onChange={(e) => {
                    setImage(e.target.files[0]);
                    uploadFileHandler(e);
                  }}
                />
              </div>
            </div>

            <div className="row">
              <div className="col-md-3 mb-3">
                <label htmlFor="cc-expiration">Quantity</label>
                <input
                  type="number"
                  className="form-control"
                  id="cc-expiration"
                  placeholder=""
                  required
                />
                <div className="invalid-feedback">Expiration date required</div>
              </div>
              <div className="col-md-3 mb-3">
                <label htmlFor="cc-expiration">Price</label>
                <input
                  type="number"
                  className="form-control"
                  id="cc-cvv"
                  placeholder=""
                  required
                />
                <div className="invalid-feedback">Security code required</div>
              </div>
              <div className="col-md-3 mb-3">
                <label htmlFor="cc-expiration">Select Subcategory</label>
                <Dropdown>
                  <Dropdown.Toggle id="dropdown-basic">
                    SubCategory
                  </Dropdown.Toggle>

                  <Dropdown.Menu>
                    {subcategories.map((subcategory, i) => {
                      return (
                        <Dropdown.Item
                          onSelect={() => {
                            handleSelect(subcategory.name);
                          }}
                        >
                          {subcategory.name}
                        </Dropdown.Item>
                      );
                    })}
                  </Dropdown.Menu>
                </Dropdown>
              </div>
            </div>
            <hr className="mb-4" />
            <button className="btn btn-primary btn-lg btn-block" type="submit">
              Continue to checkout
            </button>
          </form>
          <Button type="submit" className="me-2" variant="primary btn-lg">
            Update Product
          </Button>
        </Row>
      </Form>
    </>
  );
}
